import{_ as o,c as t,o as i,ae as r}from"./chunks/framework.BHrE6nLq.js";const u=JSON.parse('{"title":"区块链简介","description":"","frontmatter":{},"headers":[],"relativePath":"blockchain/basics/index.md","filePath":"blockchain/basics/index.md"}'),e={name:"blockchain/basics/index.md"};function n(l,a,s,c,h,d){return i(),t("div",null,a[0]||(a[0]=[r('<h1 id="区块链简介" tabindex="-1">区块链简介 <a class="header-anchor" href="#区块链简介" aria-label="Permalink to &quot;区块链简介&quot;">​</a></h1><h2 id="什么是区块链" tabindex="-1">什么是区块链？ <a class="header-anchor" href="#什么是区块链" aria-label="Permalink to &quot;什么是区块链？&quot;">​</a></h2><p>区块链（Blockchain）是一种<strong>分布式账本技术（DLT）</strong>，其特点是去中心化、不可篡改、透明安全。区块链最早由中本聪（Satoshi Nakamoto）在 2008 年提出，并作为比特币（Bitcoin）的底层技术。</p><h2 id="区块链的核心特点" tabindex="-1">区块链的核心特点 <a class="header-anchor" href="#区块链的核心特点" aria-label="Permalink to &quot;区块链的核心特点&quot;">​</a></h2><ol><li><strong>去中心化</strong>：数据存储在多个节点中，无需中央机构管理。</li><li><strong>不可篡改</strong>：交易一旦被记录，几乎不可能被篡改。</li><li><strong>透明性</strong>：所有交易数据对网络中的所有人可见。</li><li><strong>安全性</strong>：基于密码学算法（如哈希函数、非对称加密）确保数据安全。</li><li><strong>智能合约</strong>：基于区块链的自动执行代码，提高交易效率。</li></ol><h2 id="区块链的分类" tabindex="-1">区块链的分类 <a class="header-anchor" href="#区块链的分类" aria-label="Permalink to &quot;区块链的分类&quot;">​</a></h2><ul><li><strong>公有链（Public Blockchain）</strong>：任何人都可以参与，如比特币、以太坊。</li><li><strong>私有链（Private Blockchain）</strong>：仅授权用户可访问，如企业内部链。</li><li><strong>联盟链（Consortium Blockchain）</strong>：由多个机构共同维护，如 Hyperledger Fabric。</li></ul><h2 id="区块链的应用" tabindex="-1">区块链的应用 <a class="header-anchor" href="#区块链的应用" aria-label="Permalink to &quot;区块链的应用&quot;">​</a></h2><ul><li><strong>金融领域</strong>：跨境支付、去中心化金融（DeFi）。</li><li><strong>供应链管理</strong>：商品溯源，确保数据真实可靠。</li><li><strong>数字身份</strong>：去中心化身份（DID），增强隐私保护。</li><li><strong>NFT（非同质化代币）</strong>：数字艺术品、虚拟资产的确权与交易。</li></ul><h2 id="未来展望" tabindex="-1">未来展望 <a class="header-anchor" href="#未来展望" aria-label="Permalink to &quot;未来展望&quot;">​</a></h2><p>区块链技术正在向 Web3.0、元宇宙、隐私计算等方向发展，未来可能深刻影响全球经济和社会结构。</p>',11)]))}const b=o(e,[["render",n]]);export{u as __pageData,b as default};
