import{_ as r,c as d,o as e,ae as a}from"./chunks/framework.BHrE6nLq.js";const b=JSON.parse('{"title":"术语词汇全解析","description":"","frontmatter":{},"headers":[],"relativePath":"blockchain/basics/terminology.md","filePath":"blockchain/basics/terminology.md"}'),o={name:"blockchain/basics/terminology.md"};function n(s,t,h,i,l,g){return e(),d("div",null,t[0]||(t[0]=[a('<h1 id="术语词汇全解析" tabindex="-1">术语词汇全解析 <a class="header-anchor" href="#术语词汇全解析" aria-label="Permalink to &quot;术语词汇全解析&quot;">​</a></h1><blockquote><p>一站式掌握你在炒币/研究项目中需要知道的关键概念，包括链上数据、项目机制、市场操作行为等，适用于新手进阶用户。</p></blockquote><hr><h2 id="🧩-1-基础-defi-指标术语" tabindex="-1">🧩 1. 基础 DeFi 指标术语 <a class="header-anchor" href="#🧩-1-基础-defi-指标术语" aria-label="Permalink to &quot;🧩 1. 基础 DeFi 指标术语&quot;">​</a></h2><table tabindex="0"><thead><tr><th>术语</th><th>含义说明</th></tr></thead><tbody><tr><td><strong>Liquidity</strong>（流动性）</td><td>某个交易对中锁定的资产总价值，决定交易滑点、成交深度。</td></tr><tr><td><strong>TVL</strong>（总锁仓量）</td><td>协议中所有流动性池锁定的资金总量，是项目规模与安全性的重要指标。</td></tr><tr><td><strong>Market Cap</strong>（市值）</td><td>现流通代币数量 * 当前价格，用于衡量代币规模。</td></tr><tr><td><strong>FDV</strong>（完全稀释估值）</td><td>总发行量 * 当前价格，表示项目理论市值上限。</td></tr><tr><td><strong>Volume</strong>（24H交易量）</td><td>某币过去24小时的交易额，判断市场活跃度。</td></tr><tr><td><strong>Price</strong>（价格）</td><td>当前代币的市场价格。</td></tr><tr><td><strong>Slippage</strong>（滑点）</td><td>预期价格与实际成交价格的差值。</td></tr><tr><td><strong>Holders</strong>（持币地址）</td><td>当前持币的钱包数量，判断用户分布。</td></tr><tr><td><strong>Pair Age</strong>（交易对年龄）</td><td>该币种上线链上的时间。</td></tr></tbody></table><hr><h2 id="🔍-2-数据查询推荐平台" tabindex="-1">🔍 2. 数据查询推荐平台 <a class="header-anchor" href="#🔍-2-数据查询推荐平台" aria-label="Permalink to &quot;🔍 2. 数据查询推荐平台&quot;">​</a></h2><table tabindex="0"><thead><tr><th>名称</th><th>用途</th><th>链接</th></tr></thead><tbody><tr><td>Dexscreener</td><td>实时价格、K线、成交记录</td><td><a href="https://dexscreener.com" target="_blank" rel="noreferrer">https://dexscreener.com</a></td></tr><tr><td>DeBank</td><td>钱包资产、TVL、社交分析</td><td><a href="https://debank.com" target="_blank" rel="noreferrer">https://debank.com</a></td></tr><tr><td>DefiLlama</td><td>各协议/链的TVL对比</td><td><a href="https://defillama.com" target="_blank" rel="noreferrer">https://defillama.com</a></td></tr><tr><td>CoinGecko</td><td>项目市值、FDV、链上信息</td><td><a href="https://www.coingecko.com" target="_blank" rel="noreferrer">https://www.coingecko.com</a></td></tr><tr><td>BscScan</td><td>BSC区块链浏览器，查看合约、交易</td><td><a href="https://bscscan.com" target="_blank" rel="noreferrer">https://bscscan.com</a></td></tr><tr><td>Etherscan</td><td>Ethereum链浏览器</td><td><a href="https://etherscan.io" target="_blank" rel="noreferrer">https://etherscan.io</a></td></tr></tbody></table><hr><h2 id="🧮-3-项目机制相关术语" tabindex="-1">🧮 3. 项目机制相关术语 <a class="header-anchor" href="#🧮-3-项目机制相关术语" aria-label="Permalink to &quot;🧮 3. 项目机制相关术语&quot;">​</a></h2><table tabindex="0"><thead><tr><th>术语</th><th>含义</th></tr></thead><tbody><tr><td><strong>Tokenomics</strong></td><td>项目的代币经济设计，包括分配机制、释放规则等。</td></tr><tr><td><strong>Mint Function</strong></td><td>合约是否可以铸造（增发）新代币，若未禁用，潜在风险高。</td></tr><tr><td><strong>Vesting</strong></td><td>团队/投资人代币释放时间表，防止集中抛售。</td></tr><tr><td><strong>Liquidity Lock</strong></td><td>是否锁定流动性，防止项目方撤资跑路。</td></tr><tr><td><strong>Audit</strong></td><td>合约是否经过第三方审计，如 CertiK、SlowMist 等。</td></tr></tbody></table><hr><h2 id="🔗-4-链上行为与工具术语" tabindex="-1">🔗 4. 链上行为与工具术语 <a class="header-anchor" href="#🔗-4-链上行为与工具术语" aria-label="Permalink to &quot;🔗 4. 链上行为与工具术语&quot;">​</a></h2><table tabindex="0"><thead><tr><th>术语</th><th>含义</th></tr></thead><tbody><tr><td><strong>Wallet Address</strong></td><td>钱包地址，通常以 0x 开头。</td></tr><tr><td><strong>Contract Address</strong></td><td>代币或协议的智能合约地址。</td></tr><tr><td><strong>Bridge</strong></td><td>跨链桥，跨链转移资产。</td></tr><tr><td><strong>Gas Fee</strong></td><td>执行交易需支付的手续费，和链拥堵程度有关。</td></tr><tr><td><strong>Explorer</strong></td><td>区块链浏览器，如 Etherscan/BscScan。</td></tr></tbody></table><h2 id="⚠️-5-风险识别术语" tabindex="-1">⚠️ 5. 风险识别术语 <a class="header-anchor" href="#⚠️-5-风险识别术语" aria-label="Permalink to &quot;⚠️ 5. 风险识别术语&quot;">​</a></h2><table tabindex="0"><thead><tr><th>术语</th><th>含义</th></tr></thead><tbody><tr><td><strong>Rug Pull</strong></td><td>项目方撤出流动性或锁仓，导致币价归零。</td></tr><tr><td><strong>Honeypot</strong></td><td>看似正常但买入后无法卖出的欺诈合约。</td></tr><tr><td><strong>Whale</strong></td><td>大户钱包，持币比例高时可能操控市场。</td></tr><tr><td><strong>Whitelist</strong></td><td>预售白名单，有资格提前参与代币发行。</td></tr><tr><td><strong>Airdrop</strong></td><td>项目方赠送代币以吸引用户或奖励持有者。</td></tr></tbody></table><hr><h2 id="📉-6-炒币行为类术语-交易心理-市场状态" tabindex="-1">📉 6. 炒币行为类术语（交易心理 &amp; 市场状态） <a class="header-anchor" href="#📉-6-炒币行为类术语-交易心理-市场状态" aria-label="Permalink to &quot;📉 6. 炒币行为类术语（交易心理 &amp; 市场状态）&quot;">​</a></h2><table tabindex="0"><thead><tr><th>术语</th><th>含义说明</th></tr></thead><tbody><tr><td><strong>横盘</strong>（Sideways）</td><td>市场价格在一个区间反复震荡，没有明确趋势。</td></tr><tr><td><strong>砸盘</strong>（Dump）</td><td>主力或大户集中抛售造成币价快速下跌。</td></tr><tr><td><strong>抄底</strong>（Buy the Dip）</td><td>在价格下跌后低价买入，预期后续上涨。</td></tr><tr><td><strong>拉盘</strong>（Pump）</td><td>大量资金集中买入，快速推高币价。</td></tr><tr><td><strong>踏空</strong>（Missed Out）</td><td>市场上涨时未参与投资，感到懊悔。</td></tr><tr><td><strong>套牢</strong>（Bagholding）</td><td>在高点买入后价格下跌而无法及时止损。</td></tr><tr><td><strong>割肉</strong>（Cut Loss）</td><td>在亏损情况下主动平仓或卖出止损。</td></tr><tr><td><strong>反弹</strong>（Rebound）</td><td>跌势后的短暂上涨，多为技术修复。</td></tr><tr><td><strong>回调</strong>（Pullback）</td><td>上涨趋势中的临时下跌。</td></tr><tr><td><strong>熔断</strong>（Circuit Breaker）</td><td>市场暴跌时触发的自动暂停机制（中心化交易所用较多）。</td></tr></tbody></table>',19)]))}const m=r(o,[["render",n]]);export{b as __pageData,m as default};
