import{_ as r,c as o,o as a,ae as i}from"./chunks/framework.BHrE6nLq.js";const l="/images/blockchain/%E4%B8%AD%E6%9C%AC%E8%81%AA.jpg",u=JSON.parse('{"title":"区块链诞生-比特币","description":"","frontmatter":{},"headers":[],"relativePath":"blockchain/history/bitcoin_creation.md","filePath":"blockchain/history/bitcoin_creation.md"}'),n={name:"blockchain/history/bitcoin_creation.md"};function e(s,t,d,h,c,g){return a(),o("div",null,t[0]||(t[0]=[i('<h1 id="区块链诞生-比特币" tabindex="-1">区块链诞生-比特币 <a class="header-anchor" href="#区块链诞生-比特币" aria-label="Permalink to &quot;区块链诞生-比特币&quot;">​</a></h1><p>谜一样的人--<strong>中本聪</strong></p><p><img src="'+l+'" alt="Vitalik Buterin"></p><h2 id="一、定义与起源" tabindex="-1">一、定义与起源 <a class="header-anchor" href="#一、定义与起源" aria-label="Permalink to &quot;一、定义与起源&quot;">​</a></h2><p>比特币1.0时代（2008-2015年）是区块链技术的初始阶段，以<strong>比特币（BTC）<strong>为核心代表，实现了</strong>去中心化数字货币</strong>的首次应用。这一阶段由中本聪（Satoshi Nakamoto）在2008年发布的《比特币：一种点对点电子现金系统》白皮书中提出。</p><h2 id="二、技术架构" tabindex="-1">二、技术架构 <a class="header-anchor" href="#二、技术架构" aria-label="Permalink to &quot;二、技术架构&quot;">​</a></h2><ol><li><p><strong>区块链基础</strong></p><ul><li>比特币采用<strong>分布式账本技术</strong>，所有交易记录通过加密算法链接成不可篡改的链式结构。</li><li>数据由全网节点共享维护，无需中心化机构参与验证。</li></ul></li><li><p><strong>共识机制</strong></p><ul><li>通过**工作量证明（PoW）**实现网络共识，矿工通过计算哈希值竞争记账权，每10分钟生成一个新区块。</li></ul></li><li><p><strong>供应机制</strong></p><ul><li>总量固定为<strong>2100万枚</strong>，通过“减半”机制控制通胀（如2009年区块奖励为50 BTC，2012年首次减半至25 BTC）。</li></ul></li></ol><hr><h2 id="三、关键特征" tabindex="-1">三、关键特征 <a class="header-anchor" href="#三、关键特征" aria-label="Permalink to &quot;三、关键特征&quot;">​</a></h2><ol><li><p><strong>去中心化支付</strong></p><ul><li>实现点对点交易，消除传统金融机构的中介角色。</li><li>典型应用：2010年首次实物交易（程序员用1万BTC购买披萨）。</li></ul></li><li><p><strong>匿名性与安全性</strong></p><ul><li>交易通过<strong>公钥地址</strong>完成，用户身份不直接暴露。</li><li>私钥加密技术保障资产所有权。</li></ul></li><li><p><strong>技术局限性</strong></p><ul><li>仅支持简单转账功能，无法处理复杂逻辑（如智能合约）。</li><li>交易处理效率低（约每秒7笔），区块大小限制引发扩容争议。</li></ul></li></ol><hr><h2 id="四、重要事件" tabindex="-1">四、重要事件 <a class="header-anchor" href="#四、重要事件" aria-label="Permalink to &quot;四、重要事件&quot;">​</a></h2><table tabindex="0"><thead><tr><th>时间</th><th>事件描述</th><th>影响</th></tr></thead><tbody><tr><td><strong>2008.10</strong></td><td>中本聪发布比特币白皮书</td><td>奠定区块链技术理论基础</td></tr><tr><td><strong>2009.01</strong></td><td>创世区块（Genesis Block）诞生</td><td>比特币网络正式启动</td></tr><tr><td><strong>2010.05</strong></td><td>首个交易所BitcoinMarket.com 成立</td><td>开启比特币流通市场</td></tr><tr><td><strong>2013.11</strong></td><td>价格首次突破1000美元</td><td>引发全球关注</td></tr></tbody></table><hr><h2 id="五、历史意义与局限" tabindex="-1">五、历史意义与局限 <a class="header-anchor" href="#五、历史意义与局限" aria-label="Permalink to &quot;五、历史意义与局限&quot;">​</a></h2><ul><li><p><strong>意义</strong>：<br> 比特币1.0时代验证了去中心化货币的可行性，为区块链技术发展奠定基础。其开源特性推动了后续以太坊（2.0）等更复杂生态的诞生。</p></li><li><p><strong>局限</strong>：<br> 功能单一、扩展性不足，难以满足商业级应用需求，促使区块链向2.0时代演进。</p></li></ul>',16)]))}const b=r(n,[["render",e]]);export{u as __pageData,b as default};
