import{_ as t,c as r,o as a,ae as l}from"./chunks/framework.BHrE6nLq.js";const o="/images/blockchain/blockchain.jpg",_=JSON.parse('{"title":"区块链发展史","description":"","frontmatter":{},"headers":[],"relativePath":"blockchain/history/index.md","filePath":"blockchain/history/index.md"}'),n={name:"blockchain/history/index.md"};function e(s,i,g,h,d,u){return a(),r("div",null,i[0]||(i[0]=[l('<h1 id="区块链发展史" tabindex="-1">区块链发展史 <a class="header-anchor" href="#区块链发展史" aria-label="Permalink to &quot;区块链发展史&quot;">​</a></h1><p>区块链技术从比特币诞生以来，经历了多个阶段的发展，从数字货币到智能合约、去中心化金融（DeFi）、NFT，再到企业级应用与Web3。</p><p><img src="'+o+'" alt="区块链"></p><h2 id="_2008年-比特币白皮书发布" tabindex="-1">2008年：比特币白皮书发布 <a class="header-anchor" href="#_2008年-比特币白皮书发布" aria-label="Permalink to &quot;2008年：比特币白皮书发布&quot;">​</a></h2><ul><li><strong>时间</strong>：2008年10月31日</li><li><strong>事件</strong>：中本聪发布了《比特币：一种点对点的电子现金系统》白皮书，提出了去中心化的数字货币的构想。</li><li><strong>背景</strong>：2008年，全球金融危机爆发，人们开始思考金融体系的安全性和透明度问题。比特币的诞生正是基于这些问题的反思，它为金融系统提供了一种全新的去中心化的替代方案。</li></ul><h2 id="_2009年-比特币网络上线" tabindex="-1">2009年：比特币网络上线 <a class="header-anchor" href="#_2009年-比特币网络上线" aria-label="Permalink to &quot;2009年：比特币网络上线&quot;">​</a></h2><ul><li><strong>时间</strong>：2009年1月3日</li><li><strong>事件</strong>：中本聪挖出了比特币的创世区块，比特币网络正式启动。</li><li><strong>背景</strong>：这标志着区块链1.0的诞生一个去中心化的数字货币体系，完全不依赖任何中央机构进行信任验证，交易通过数学算法来保证安全性。</li></ul><h2 id="_2011年-莱特币的出现" tabindex="-1">2011年：莱特币的出现 <a class="header-anchor" href="#_2011年-莱特币的出现" aria-label="Permalink to &quot;2011年：莱特币的出现&quot;">​</a></h2><ul><li><strong>时间</strong>：2011年</li><li><strong>事件</strong>：莱特币（Litecoin）等“山寨币”开始出现，为比特币提供了更多的选择和竞争。</li><li><strong>背景</strong>：比特币的成功激发了许多开发者创建类似的加密货币，这些“山寨币”有时在功能上做了不同的尝试，比如莱特币的块生成速度更快，目标是提高交易处理能力。</li></ul><h2 id="_2013年-比特币价格首次突破1000美元" tabindex="-1">2013年：比特币价格首次突破1000美元 <a class="header-anchor" href="#_2013年-比特币价格首次突破1000美元" aria-label="Permalink to &quot;2013年：比特币价格首次突破1000美元&quot;">​</a></h2><ul><li><strong>时间</strong>：2013年11月29日</li><li><strong>事件</strong>：比特币价格突破1000美元，开始吸引全球投资者的注意。</li><li><strong>背景</strong>：随着比特币逐渐被更多人认识，尤其是在一些经济不稳定的地区，比特币被视为一种“数字黄金”。</li></ul><h2 id="_2015年-以太坊的推出" tabindex="-1">2015年：以太坊的推出 <a class="header-anchor" href="#_2015年-以太坊的推出" aria-label="Permalink to &quot;2015年：以太坊的推出&quot;">​</a></h2><ul><li><strong>时间</strong>：2015年7月30日</li><li><strong>事件</strong>：以太坊正式上线，推出了智能合约和去中心化应用（DApp）的功能。</li><li><strong>背景</strong>：以太坊的出现不仅仅是另一种数字货币，它引入了智能合约的概念，让开发者能够在区块链上构建去中心化应用，开启了区块链2.0的时代。</li></ul><h2 id="_2017年-ico热潮和defi的雏形" tabindex="-1">2017年：ICO热潮和DeFi的雏形 <a class="header-anchor" href="#_2017年-ico热潮和defi的雏形" aria-label="Permalink to &quot;2017年：ICO热潮和DeFi的雏形&quot;">​</a></h2><ul><li><strong>时间</strong>：2017年</li><li><strong>事件</strong>：ICO（首次代币发行）成为一种新型的融资方式，DeFi的概念开始初现。</li><li><strong>背景</strong>：ICO热潮让大量的新项目得以融资，这不仅带动了区块链行业的快速发展，也让DeFi（去中心化金融）作为区块链2.0的关键应用开始崭露头角。</li></ul><h2 id="_2018年-defi初步应用" tabindex="-1">2018年：DeFi初步应用 <a class="header-anchor" href="#_2018年-defi初步应用" aria-label="Permalink to &quot;2018年：DeFi初步应用&quot;">​</a></h2><ul><li><strong>时间</strong>：2018年</li><li><strong>事件</strong>：去中心化金融（DeFi）项目逐渐落地，像Uniswap、Compound等平台开始出现，允许用户进行去中心化借贷、交易等金融服务。</li><li><strong>背景</strong>：随着以太坊平台的成熟，DeFi概念逐步实现。</li></ul><h2 id="_2020年-defi爆发" tabindex="-1">2020年：DeFi爆发 <a class="header-anchor" href="#_2020年-defi爆发" aria-label="Permalink to &quot;2020年：DeFi爆发&quot;">​</a></h2><ul><li><strong>时间</strong>：2020年</li><li><strong>事件</strong>：DeFi市场爆发，Uniswap、Compound 等平台的用户量和锁仓总量<strong>急剧增长</strong>。</li><li><strong>背景</strong>：2020年全球<strong>口罩</strong>爆发，传统金融市场不稳定。DeFi在数字货币市场上获得了高度关注，成为区块链2.0最大亮点之一。</li></ul><h2 id="_2020年-nft崛起" tabindex="-1">2020年：NFT崛起 <a class="header-anchor" href="#_2020年-nft崛起" aria-label="Permalink to &quot;2020年：NFT崛起&quot;">​</a></h2><ul><li><strong>时间</strong>：2020年</li><li><strong>事件</strong>：NFT（非同质化代币）开始风靡，尤其是在艺术、游戏、收藏品等领域。</li><li><strong>背景</strong>：NFT的诞生让数字艺术、进入大众视野。艺术品《Everydays: The First 5000 Days》2021年拍卖以6900万美元成交，标志着NFT进入主流市场。</li></ul><h2 id="_2021年-nft暴涨" tabindex="-1">2021年：NFT暴涨 <a class="header-anchor" href="#_2021年-nft暴涨" aria-label="Permalink to &quot;2021年：NFT暴涨&quot;">​</a></h2><ul><li><strong>时间</strong>：2021年3月</li><li><strong>事件</strong>：NFT市场迎来爆发，多个NFT项目获得数百万美元的投资和拍卖成交。</li><li><strong>背景</strong>：NFT的市场热度让区块链的应用场景再次拓展。数字艺术作品、虚拟地产、游戏道具等成为人们购买和收藏的对象。</li></ul><h2 id="_2021年-以太坊2-0上线" tabindex="-1">2021年：以太坊2.0上线 <a class="header-anchor" href="#_2021年-以太坊2-0上线" aria-label="Permalink to &quot;2021年：以太坊2.0上线&quot;">​</a></h2><ul><li><strong>时间</strong>：2021年12月1日</li><li><strong>事件</strong>：以太坊2.0正式启动，转向权益证明（PoS）共识机制，增强了以太坊的可扩展性和安全性。</li><li><strong>背景</strong>：以太坊2.0的升级是区块链3.0的一个重要标志。通过减少能耗、提高交易效率，以太坊为去中心化金融（DeFi）和NFT等应用提供了更强大的支持。</li></ul><h2 id="_2022年-ftx崩盘与luna暴雷" tabindex="-1">2022年：FTX崩盘与LUNA暴雷 <a class="header-anchor" href="#_2022年-ftx崩盘与luna暴雷" aria-label="Permalink to &quot;2022年：FTX崩盘与LUNA暴雷&quot;">​</a></h2><ul><li><strong>时间</strong>：2022年5月和11月</li><li><strong>事件</strong>：LUNA的崩盘和FTX的破产，引发了加密货币市场的巨大动荡。</li><li><strong>背景</strong>：这些事件暴露了加密市场的风险，特别是DeFi和NFT等项目的不成熟和监管的缺失，导致投资者损失惨重。</li></ul>',27)]))}const f=t(n,[["render",e]]);export{_ as __pageData,f as default};
