import{_ as s,c as t,o as r,ae as n}from"./chunks/framework.BHrE6nLq.js";const a="/images/history/Steve_Jobs_and_Macintosh_computer,_January_1984.jpg",i="/images/history/AlphaGo_Li.jpg",c=JSON.parse('{"title":"计算发展史","description":"","frontmatter":{},"headers":[],"relativePath":"network/history/computer.md","filePath":"network/history/computer.md"}'),g={name:"network/history/computer.md"};function l(e,o,p,h,_,d){return r(),t("div",null,o[0]||(o[0]=[n('<h1 id="计算发展史" tabindex="-1">计算发展史 <a class="header-anchor" href="#计算发展史" aria-label="Permalink to &quot;计算发展史&quot;">​</a></h1><h2 id="_1940s-1950s-计算机的起源" tabindex="-1">1940s - 1950s：计算机的起源 <a class="header-anchor" href="#_1940s-1950s-计算机的起源" aria-label="Permalink to &quot;1940s - 1950s：计算机的起源&quot;">​</a></h2><ul><li><p><strong>1941年</strong>：德国工程师<strong>康拉德·祖泽</strong>（Konrad Zuse）设计并建造了世界上第一台程序控制的电子计算机<strong>Z3</strong>。</p></li><li><p><strong>1945年</strong>：美国的<strong>ENIAC</strong>（电子数值积分计算机）投入使用，是世界上第一台电子通用计算机。</p></li></ul><p><img src="https://upload.wikimedia.org/wikipedia/commons/thumb/6/6c/ENIAC_Penn1.jpg/1920px-ENIAC_Penn1.jpg" alt="ENIAC"></p><ul><li><strong>1951年</strong>：第一台商用计算机<strong>UNIVAC I</strong>诞生，标志着计算机开始进入商业领域。 <img src="https://upload.wikimedia.org/wikipedia/commons/thumb/6/60/Univac_I_at_CHM.agr.jpg/1920px-Univac_I_at_CHM.agr.jpg" alt="UNIVAC I"></li></ul><h2 id="_1960s-计算机产业的崛起" tabindex="-1">1960s：计算机产业的崛起 <a class="header-anchor" href="#_1960s-计算机产业的崛起" aria-label="Permalink to &quot;1960s：计算机产业的崛起&quot;">​</a></h2><ul><li><p><strong>1969年</strong>：<strong>ARPANET(阿帕网 高级研究计划局网络)</strong>，即互联网的前身，开始建设。最初目的是为了军事和学术研究数据的共享。</p></li><li><p><strong>1969年</strong>：<strong>UNIX操作系统</strong>诞生，由<strong>Ken Thompson</strong>和<strong>Dennis Ritchie</strong>在贝尔实验室开发。UNIX后来成为许多操作系统（包括Linux和MacOS）的基础。</p></li></ul><h2 id="_1970s-个人计算机的诞生" tabindex="-1">1970s：个人计算机的诞生 <a class="header-anchor" href="#_1970s-个人计算机的诞生" aria-label="Permalink to &quot;1970s：个人计算机的诞生&quot;">​</a></h2><ul><li><p><strong>1975年</strong>：<strong>Altair 8800</strong>发布，是世界上第一款个人计算机。它以开源的方式面向爱好者，标志着个人计算机的兴起。</p></li><li><p><strong>1977年</strong>：<strong>Apple II</strong>发布，成为史上第一台广受欢迎的个人计算机。 <img src="https://upload.wikimedia.org/wikipedia/commons/thumb/c/ca/Apple_II-IMG_7064.jpg/1920px-Apple_II-IMG_7064.jpg" alt="Apple II"></p></li></ul><h2 id="_1980s-操作系统的多元化与图形界面的引入" tabindex="-1">1980s：操作系统的多元化与图形界面的引入 <a class="header-anchor" href="#_1980s-操作系统的多元化与图形界面的引入" aria-label="Permalink to &quot;1980s：操作系统的多元化与图形界面的引入&quot;">​</a></h2><ul><li><p><strong>1981年</strong>：<strong>IBM PC</strong>发布，标志着个人计算机的普及进入主流市场，并成为标准。</p></li><li><p><strong>1983年</strong>：<strong>Microsoft Windows 1.0</strong>发布第一版，开启了图形用户界面（GUI）操作系统的时代。 <img src="https://upload.wikimedia.org/wikipedia/en/4/4e/Windows1.0.png" alt="Windows 1.0"></p></li><li><p><strong>1984年</strong>：<strong>Mac OS Macintosh</strong>由<strong>苹果公司</strong>发布，带来了革命性的图形用户界面（GUI），使计算机更加易用。</p></li></ul><p><img src="'+a+'" alt="Mac OS Macintosh"></p><h2 id="_1990s-互联网的崛起与开源运动" tabindex="-1">1990s：互联网的崛起与开源运动 <a class="header-anchor" href="#_1990s-互联网的崛起与开源运动" aria-label="Permalink to &quot;1990s：互联网的崛起与开源运动&quot;">​</a></h2><ul><li><p><strong>1991年</strong>：<strong>Linux</strong>内核发布，由<strong>Linus Torvalds</strong>开发，开始了开源操作系统的时代，逐渐成为全球广泛使用的操作系统之一。</p></li><li><p><strong>1995年</strong>：<strong>Windows 95</strong>发布，带来了全新的用户体验，如开始菜单和多任务处理，成为Windows家族中的一个里程碑。</p></li><li><p><strong>1998年</strong>：<strong>Google</strong>成立，改变了互联网搜索和信息获取的方式。</p></li></ul><h2 id="_2000s-互联网与智能设备的普及" tabindex="-1">2000s：互联网与智能设备的普及 <a class="header-anchor" href="#_2000s-互联网与智能设备的普及" aria-label="Permalink to &quot;2000s：互联网与智能设备的普及&quot;">​</a></h2><ul><li><p><strong>2001年</strong>：<strong>Windows XP</strong>发布，成为Windows操作系统中最成功的一版，广泛应用于个人电脑、企业和教育领域。</p></li><li><p><strong>2007年</strong>：<strong>iPhone</strong>发布，标志着智能手机时代的开始，影响了全球数十亿人的生活和工作方式。</p></li><li><p><strong>2009年</strong>：<strong>比特币</strong>的创始人<strong>中本聪</strong>（Satoshi Nakamoto）发布了比特币白皮书，推出了全球第一个去中心化的数字货币<strong>比特币（BTC）</strong>。比特币基于区块链技术，开启了数字货币和加密金融的新时代。</p></li></ul><p><img src="https://upload.wikimedia.org/wikipedia/commons/thumb/c/ce/Bust_of_Satoshi_Nakamoto_in_Budapest.jpg/800px-Bust_of_Satoshi_Nakamoto_in_Budapest.jpg" alt="中本聪"></p><h2 id="_2010s-云计算与人工智能的崛起" tabindex="-1">2010s：云计算与人工智能的崛起 <a class="header-anchor" href="#_2010s-云计算与人工智能的崛起" aria-label="Permalink to &quot;2010s：云计算与人工智能的崛起&quot;">​</a></h2><ul><li><p><strong>2010年</strong>：<strong>Apple iPad</strong>发布，开启了平板电脑市场，并推动了移动计算的普及。</p></li><li><p><strong>2014年</strong>：<strong>Docker</strong>发布，改变了开发和部署应用程序的方式，推动了容器化技术的发展。</p></li><li><p><strong>2016年</strong>：<strong>Google DeepMind的AlphaGo</strong>成功击败了世界围棋冠军<strong>李世石</strong>，AI 攻破了最难的游戏。</p></li></ul><p><img src="'+i+'" alt="AlphaGo"></p><ul><li><p><strong>2017年</strong>：<strong>区块链技术</strong>进入主流市场，<strong>比特币</strong>的崛起引发了全球对加密货币和分布式账本技术的关注。随后，<strong>以太坊</strong>、<strong>智能合约</strong>和**去中心化金融（DeFi）**等概念相继发展。</p></li><li><p><strong>2020年</strong>：<strong>GPT-3</strong>发布，其庞大的语言模型在自然语言处理、机器翻译、文本生成等领域展现出前所未有的能力，并逐渐开始应用于商业、教育等多个行业。</p></li><li><p><strong>2021年</strong>：<strong>区块链</strong>技术的应用范围扩大，非同质化代币（NFT）爆发式增长，成为数字艺术和虚拟物品交易的新兴市场。</p></li></ul><h2 id="_2020s-云原生与量子计算" tabindex="-1">2020s：云原生与量子计算 <a class="header-anchor" href="#_2020s-云原生与量子计算" aria-label="Permalink to &quot;2020s：云原生与量子计算&quot;">​</a></h2><ul><li><strong>2025年</strong>：<strong>Deepseek</strong> 火爆全网，英伟达股票大跌，AI 领域迎来新的突破。</li></ul>',23)]))}const m=s(g,[["render",l]]);export{c as __pageData,m as default};
