import{_ as t,c as s,o as i,ae as e}from"./chunks/framework.BHrE6nLq.js";const g=JSON.parse('{"title":"链路聚合","description":"","frontmatter":{},"headers":[],"relativePath":"network/tcpip/layer2/lacp.md","filePath":"network/tcpip/layer2/lacp.md"}'),l={name:"network/tcpip/layer2/lacp.md"};function n(r,a,p,o,h,d){return i(),s("div",null,a[0]||(a[0]=[e(`<h1 id="链路聚合" tabindex="-1">链路聚合 <a class="header-anchor" href="#链路聚合" aria-label="Permalink to &quot;链路聚合&quot;">​</a></h1><p>链路聚合是一种将多个物理链路组合成一条逻辑链路的技术，可以提升带宽和冗余性。链路聚合协议主要包括 <strong>LACP</strong>（基于 IEEE 标准）和 <strong>PAGP</strong>（Cisco 专有协议）。</p><h3 id="共有协议-lacp" tabindex="-1">共有协议：LACP <a class="header-anchor" href="#共有协议-lacp" aria-label="Permalink to &quot;共有协议：LACP&quot;">​</a></h3><p>LACP（Link Aggregation Control Protocol，链路汇聚控制协议）基于 IEEE 802.3ad 标准，负责动态汇聚链路，具备自动化和灵活性的特点。</p><ul><li><strong>LACP 协议数据单元（LACPDU）</strong>：<br> LACP 通过 LACPDU 与对端设备交换信息，用于链路汇聚和状态维护。</li><li><strong>LACP 工作机制</strong>： <ul><li>每 30 秒发送一次 LACPDU，用于维持链路状态。</li><li>支持链路冗余，当一条链路失效时，自动将流量切换到其他链路。</li></ul></li></ul><h3 id="私有协议-pagp" tabindex="-1">私有协议：PAGP <a class="header-anchor" href="#私有协议-pagp" aria-label="Permalink to &quot;私有协议：PAGP&quot;">​</a></h3><p>PAGP（Port Aggregation Protocol，端口聚合协议）是 Cisco 专有的链路聚合协议，专门用于管理 EtherChannel 链路。</p><ul><li><strong>PAGP 数据包</strong>： <ul><li>每 30 秒发送一次 PAGP 数据包，用于维持链路状态和检测两端配置。</li></ul></li><li><strong>PAGP 工作机制</strong>： <ul><li>检测链路两端的配置是否一致，确保链路兼容。</li><li>自动添加或移除链路，维持 EtherChannel 链路的正常工作。</li></ul></li></ul><h3 id="lacp-与-pagp-的主要区别" tabindex="-1">LACP 与 PAGP 的主要区别 <a class="header-anchor" href="#lacp-与-pagp-的主要区别" aria-label="Permalink to &quot;LACP 与 PAGP 的主要区别&quot;">​</a></h3><table tabindex="0"><thead><tr><th><strong>特性</strong></th><th><strong>LACP</strong></th><th><strong>PAGP</strong></th></tr></thead><tbody><tr><td>标准化</td><td>IEEE 802.3ad</td><td>Cisco 专有</td></tr><tr><td>数据包类型</td><td>LACPDU</td><td>PAGP 数据包</td></tr><tr><td>兼容性</td><td>跨厂商设备</td><td>仅限 Cisco 设备</td></tr><tr><td>动态链路调整</td><td>支持</td><td>支持</td></tr></tbody></table><h3 id="链路聚合配置示例-lacp-和-pagp" tabindex="-1">链路聚合配置示例（LACP 和 PAGP） <a class="header-anchor" href="#链路聚合配置示例-lacp-和-pagp" aria-label="Permalink to &quot;链路聚合配置示例（LACP 和 PAGP）&quot;">​</a></h3><h4 id="lacp-配置示例" tabindex="-1"><strong>LACP 配置示例</strong> <a class="header-anchor" href="#lacp-配置示例" aria-label="Permalink to &quot;**LACP 配置示例**&quot;">​</a></h4><ol><li>在接口上启用链路聚合：<div class="language-shell vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">interface</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> range</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> g0/1</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> -</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> 2</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">channel-group</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> 1</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> mode</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> active</span></span></code></pre></div></li><li>查看链路聚合状态：<div class="language-shell vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">show</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> etherchannel</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> summary</span></span></code></pre></div></li></ol><h4 id="pagp-配置示例" tabindex="-1"><strong>PAGP 配置示例</strong> <a class="header-anchor" href="#pagp-配置示例" aria-label="Permalink to &quot;**PAGP 配置示例**&quot;">​</a></h4><ol><li>在接口上启用 PAGP<div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>interface range g0/1 - 2</span></span>
<span class="line"><span>channel-group 1 mode desirable</span></span></code></pre></div></li><li>查看链路聚合状态：<div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>show etherchannel detail</span></span></code></pre></div></li></ol>`,15)]))}const P=t(l,[["render",n]]);export{g as __pageData,P as default};
