import{_ as e,c as a,o as r,ae as o}from"./chunks/framework.BHrE6nLq.js";const c=JSON.parse('{"title":"Anytype 笔记介绍","description":"","frontmatter":{},"headers":[],"relativePath":"treasure_box/anytype/index.md","filePath":"treasure_box/anytype/index.md"}'),n={name:"treasure_box/anytype/index.md"};function d(l,t,i,s,p,h){return r(),a("div",null,t[0]||(t[0]=[o('<h1 id="anytype-笔记介绍" tabindex="-1">Anytype 笔记介绍 <a class="header-anchor" href="#anytype-笔记介绍" aria-label="Permalink to &quot;Anytype 笔记介绍&quot;">​</a></h1><h2 id="什么是-anytype" tabindex="-1">什么是 Anytype？ <a class="header-anchor" href="#什么是-anytype" aria-label="Permalink to &quot;什么是 Anytype？&quot;">​</a></h2><p><a href="https://anytype.io/" target="_blank" rel="noreferrer">Anytype</a> 是一款<strong>去中心化、隐私优先、结构化设计</strong>的笔记与知识管理软件，旨在提供一个不依赖云端、可本地运行的个人知识操作系统（PKOS: Personal Knowledge Operating System）。</p><h2 id="🧷-功能简介-对标-notion" tabindex="-1">🧷 功能简介（对标 Notion） <a class="header-anchor" href="#🧷-功能简介-对标-notion" aria-label="Permalink to &quot;🧷 功能简介（对标 Notion）&quot;">​</a></h2><table tabindex="0"><thead><tr><th>功能模块</th><th>Anytype</th><th>Notion</th></tr></thead><tbody><tr><td>页面 &amp; Block</td><td>✅ 类似 Notion，支持多种 Block</td><td>✅ 丰富的 Block 类型</td></tr><tr><td>数据库系统</td><td>✅ 使用 Set/Collection 构建数据库</td><td>✅ 表格、看板、日历等视图</td></tr><tr><td>模板功能</td><td>✅ 可自定义类型与模板</td><td>✅ 强大的模板库</td></tr><tr><td>多端同步</td><td>✅ 去中心化同步（IPFS）</td><td>✅ 云同步</td></tr><tr><td>插件扩展</td><td>🚧 暂未开放插件系统（规划中）</td><td>✅ 支持集成/插件</td></tr><tr><td>图谱视图</td><td>✅ 内建关联图谱</td><td>❌ 无原生图谱</td></tr><tr><td>网络协作</td><td>🚧 尚不支持多人协作</td><td>✅ 支持团队协作</td></tr></tbody></table><hr><h2 id="开源情况" tabindex="-1">开源情况 <a class="header-anchor" href="#开源情况" aria-label="Permalink to &quot;开源情况&quot;">​</a></h2><ul><li><p><strong>目前是否开源？</strong><br> ❌ <strong>尚未完全开源</strong>，但开发团队承诺会在未来逐步开放核心组件的源代码（计划在正式版之后逐步实施）。</p></li><li><p><strong>使用协议</strong></p><ul><li>免费使用，无广告，无需订阅</li><li>不需要注册账户，使用身份密钥进行设备绑定</li><li>数据完全保存在本地，可导出为 Markdown + 媒体资源</li></ul></li></ul><hr><h1 id="anytype-托管模式与自托管模式" tabindex="-1">Anytype：托管模式与自托管模式 <a class="header-anchor" href="#anytype-托管模式与自托管模式" aria-label="Permalink to &quot;Anytype：托管模式与自托管模式&quot;">​</a></h1><h2 id="一、托管模式" tabindex="-1">一、托管模式 <a class="header-anchor" href="#一、托管模式" aria-label="Permalink to &quot;一、托管模式&quot;">​</a></h2><p>数据在<strong>本地加密存储</strong>，通过Anytype官方服务器进行P2P加密同步，默认提供 <strong>1GB免费云空间</strong>。</p><blockquote><p><strong>优势</strong>：操作简单，兼顾安全与便利<br><strong>局限</strong>：依赖官方服务器，容量有限</p></blockquote><h2 id="二、自托管模式" tabindex="-1">二、自托管模式 <a class="header-anchor" href="#二、自托管模式" aria-label="Permalink to &quot;二、自托管模式&quot;">​</a></h2><p>用户<strong>自行搭建服务器</strong>（如NAS/私有云），数据完全脱离Anytype官方服务，仅在私有网络流转。</p><blockquote><p><strong>优势</strong>：数据完全私有化，无厂商绑定<br><strong>局限</strong>：部署复杂，需持续运维</p></blockquote><h2 id="下载与体验" tabindex="-1">下载与体验 <a class="header-anchor" href="#下载与体验" aria-label="Permalink to &quot;下载与体验&quot;">​</a></h2><p>你可以从官网下载安装最新版本：</p><p>👉 <a href="https://anytype.io/" target="_blank" rel="noreferrer">https://anytype.io/</a></p><p>支持平台：</p><ul><li>🖥️ Windows / macOS / Linux</li><li>📱 iOS / Android（需通过 TestFlight 或 APK）</li></ul>',21)]))}const u=e(n,[["render",d]]);export{c as __pageData,u as default};
